import React from 'react'
import {Chart} from 'react-google-charts'

class MainPieChart extends React.Component{
    constructor(props){
        super(props);
        this.state={
            data: this.props.pieChartData,
        }
    }
    render(){
        return(
        <div className='mainPieChart'>
        <Chart
        width={'800px'}
        height={'300px'}
         chartType="PieChart"
         loader={<div>Loading Chart</div>}
         /*
         data={[
           ['Expense', 'Dollars'],
           ['Phone', 110],
           ['Food', 200],
           ['Gas', 250],
           ['Car', 600],
           ['Rent', 700],
         ]}
         */
         data={this.state.data}
         options={{
           title: 'Expenses',
           pieHole: 0.4,
           is3D: false
         }}
         rootProps={{ 'data-testid': '1' }}
        />
        </div>
        )
    }
}

export default MainPieChart
