import React from 'react'
import Aside from './Components/Aside'
import Main from './Components/Main'


class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: 'David',
            pieChartData: [['expense', 'dollars'],['this', 400], ['that',700],['somehting',20]],
            barChartData: [['expense', 'dollars'],['this', 400], ['that',700],['somehting',20]],
        }
    }


    componentWillMount(){
        this.setState({pieChartData: ['david', 200]});
    }

	render(){
		return(
			<div className='container'>
                <Aside expense={expense=> this.expense = expense} amount={amount=>this.amount=amount} formSubmit={this.addToCharts} />
                <Main pieChartData={this.state.pieChartData} />
			</div>
		)
	}
}

export default App
