import React from 'react'
import NavBar from './Components/NavBar'
import IncomeExpense from './Components/IncomeExpense'

class HomePageIndex extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            TotalIncome: 0,
            TotalExpenses: 0,
            AllExpenses: [],
            NameAndExpense: [
                ['Expense', 'amount'],
            ]
        }
        this.getExpenseAmountAndName = this.getExpenseAmountAndName.bind(this);
    }

    getExpenseAmountAndName = ()=>{
        var expenseName= this.nameInput.value;
        var expenseAmount = parseInt(this.expenseInput.value);
        var total = parseInt(this.state.Income) + expenseAmount;
        var nameAndExpense = [...this.state.GoogleCharts,...[expenseName, expenseAmount]]; 
        var addToExpenses = [...this.state.AllExpenses,...expenseAmount];
        this.setState({
            TotalIncome: total,
            AllExpenses: addToExpenses,
            INcome
        })
    }

    render(){
        return(
        <div className='HomePageIndex'>
            <NavBar />
            <IncomeExpense income={this.state.TotalIncome} incomeMinusExpenses={this.state.IncomeMinusExpenses} expenseNameInput={inputName=> this.nameInput= inputName} expenseAmountInput={inputAmount=> this.expenseInput= inputAmount} submitAction={this.alertHello} />
        </div>
        )
    }
}

export default HomePageIndex
