import React from 'react'
import Aside from './Components/Aside'
import Main from './Components/Main'


class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: 'David',
            pieChartData: [['expense', 'dollars'],['this', 400], ['that',700],['somehting',20]],
            barChartData: [['expense', 'dollars'],['this', 400], ['that',700],['somehting',20]],
        };
    }

    componentWillMount(){
    }

    addToBothCharts(e){
        this.setState({pieChartData: [e.target.expense.target, e.target.amount.target]})
    }

	render(){
		return(
			<div className='container'>
				<Aside />
				<Main pieChartData={this.state.pieChartData} barChartData={this.state.barChartData} name={this.state.name} />
			</div>
		)
	}
}

export default App
