import React from 'react'
import NavBar from './Components/NavBar'
import IncomeExpense from './Components/IncomeExpense'

class HomePageIndex extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            TotalIncome: 0,
            TotalExpense: 0,
            AllExpenses: [],
            NameAndExpenses: [
                ['Expense', 'amount'],
            ]
        }
        this.getExpenseAmountAndName = this.getExpenseAmountAndName.bind(this);
    }

    getExpenseAmountAndName = () =>{
        var expenseName= this.nameInput.value;
        var expenseAmount = parseInt(this.expenseInput.value);

        var addToTotalIncome = parseInt(this.state.Income) + expenseAmount;
        var addToTotalExpenses = parseInt(this.state.TotalExpense) + expenseAmount;
        var addToAllExpenses = [...this.state.AllExpenses,...expenseAmount];
        var addToNameAndExpenses = [...this.state.NameAndExpenses,...[expenseName, expenseAmount]]; 

        this.setState({
            TotalIncome: addToTotalIncome,
            TotalExpense: addToTotalExpenses,
            AllExpenses: addToAllExpenses,
            NameAndExpenses: addToNameAndExpenses
        })
    }

    render(){
        return(
        <div className='HomePageIndex'>
            <NavBar />
            <IncomeExpense income={this.state.TotalIncome} incomeMinusExpenses={this.state.IncomeMinusExpenses} expenseNameInput={inputName=> this.nameInput= inputName} expenseAmountInput={inputAmount=> this.expenseInput= inputAmount} submitAction={this.alertHello} />
        </div>
        )
    }
}

export default HomePageIndex
