import { Component } from 'react'
import ContactNav from './Components/ContactNav'
import Nav from './Components/Nav'
import Main from './Components/Main'
import AddContact from './Components/AddContact'

class index extends Component{
    constructor(props){
        super(props);
        this.state = {
            fields: null,
        }
    }

    componentDidMount = async()=>{
        const cookieName = document.cookie;
        const cookieNameOnly = cookieName.replace('username=', '');
        const url = 'http://127.0.0.1:8080/getContactInfo';
        const postData = {
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'content-type': 'application/json'
            },
            body: JSON.stringify({username: cookieNameOnly})
        };
        const fetchData = await fetch(url, postData);
        const data = await fetchData.json();
        console.log(data);
        this.setState({fields: data});
    }

    render(){
        if (!document.cookie){
            return <h1>not logged in</h1>;
        }
        else if (this.state.fields == null) {
            return <h1>loading</h1>
        }
        return(
            <div className='ContactsPage'>
            <h1>{this.state.fields[0].name}</h1>
                <Nav />
                <div className='ContactNavDisplay'>
                    <ContactNav />
                    <div className='showHide'>
                    <AddContact name={name=> this.state.formName = name} street={street=> this.state.formStreet = street} city={city=> this.state.formCity = city} email={email=> this.state.formEmail = email}  formSubmit={this.submitForm} />
                    </div>
                </div>
                <Main />
            </div>
        )
    }
}

export default index
