import { Component } from 'react'
import ContactNav from './Components/ContactNav'
import Nav from './Components/Nav'
import Main from './Components/Main'
import AddContact from './Components/AddContact'

class index extends Component{
    constructor(props){
        super(props);
        this.state = {
        }
        this.submitForm = this.submitForm.bind(this);
    }

    submitForm = async (e)=>{
        e.preventDefault();
        const name = await this.state.formName.value;
        const street = await this.state.formStreet.value;
        const city = await this.state.formCity.value;
        const email = await this.state.formEmail.value;
        const fetchPost = await fetch('http://127.0.0.1:8080/addContactInfo', {
            method: 'POST',
            headers:{
                'Accept': "application/json",
                "Content-Type": 'application/json'
            },
            body: JSON.stringify({
                'name': name,
                'street':street,
                'city':city,
                'email':email
            })
        })
        const data = await fetchPost.json();
        console.log(data);
    }




    render(){
        return(
            <div className='ContactsPage'>
                <AddContact name={name=> this.state.formName = name} street={street=> this.state.formStreet = street} city={city=> this.state.formCity = city} email={email=> this.state.formEmail = email}  formSubmit={this.submitForm} />
            </div>
        )
    }
}

export default index
