import { Component} from 'react'
import Nav from './Components/Nav'
import phoneBottom from './Components/phoneBottom.jpg'
import SignUpIn from './Components/SignUpIn'

class SignUpInPage extends Component{
    constructor(props){
        super(props);
        this.state = {
        }
        this.signInButton = this.signInButton.bind(this);
        this.logInButton = this.logInButton.bind(this);
    }

    signInButton = async()=>{
        const userName = this.userNameRef.value;
        const passWord = this.passWordRef.value;
        alert('signInButton');
        alert(userName);
        alert(passWord);
    }

    logInButton = async()=>{
        const userName = this.userNameRef.value;
        const passWord = this.passWordRef.value;
        const fetchUser = await fetch('http://127.0.0.1:8080/user', {
            method: 'POST',
            headers:{
                'Accept': "application/json",
                "Content-Type": 'application/json'
            },
            body: JSON.stringify({
                username: userName,
            })
        })
        const data = await fetchUser.json();
        const password = await data[0].password;
        console.log(password);
        if (password == passWord){
                const addUser = await fetch('http://127.0.0.1:8080/validateUser', {
                method: 'POST',
                headers:{
                    'Accept': "application/json",
                    "Content-Type": 'application/json'
                },
                body: JSON.stringify({
                    username: userName,
                })
            })
        } else {
            console.log('nada');
        }
    }

    render(){
        return(
            <div className='SignUpInPage'>
                <Nav />
                <h1>Sign in to enter Contacts Page</h1>
                <SignUpIn logIn={this.logInButton} signUp={this.signInButton} userName={value=> this.userNameRef = value} passWord={value=> this.passWordRef = value} />
                <div className='phoneImg'>
                <img src={phoneBottom} />
                </div>
            </div>
        )
    }
}

export default SignUpInPage
