import { Component } from 'react'
import ContactNav from './Components/ContactNav'
import Nav from './Components/Nav'
import Main from './Components/Main'
import AddContact from './Components/AddContact'

class index extends Component{
    constructor(props){
        super(props);
        this.state = {
        }
        this.submitForm = this.submitForm.bind(this);
    }

    submitForm = async (e)=>{
        e.preventDefault();
        const Fname = await this.state.formName.value;
        const Fstreet = await this.state.formStreet.value;
        const Fcity = await this.state.formCity.value;
        const Femail = await this.state.formEmail.value;
        const fetchPost = await fetch('http://127.0.0.1:8080/addContactInfo', {
            method: 'POST',
            headers:{
                'Accept': "application/json",
                "Content-Type": 'application/json'
            },
            body: JSON.stringify({
                name: Fname,
                street: Fstreet,
                city: Fcity,
                email: Femail
            })
        })
        const data = await fetchPost.text();
        console.log(data);
    }




    render(){
        return(
            <div className='ContactsPage'>
                <Nav />
                <ContactNav />
                <AddContact name={name=> this.state.formName = name} street={street=> this.state.formStreet = street} city={city=> this.state.formCity = city} email={email=> this.state.formEmail = email}  formSubmit={this.submitForm} />
                <Main />
            </div>
        )
    }
}

export default index
