import React from 'react'
import SearchBar from './Components/SearchBar'

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            search: null,
        }
        this.searchQueryAction = this.searchQueryAction.bind(this);
    }

    searchQueryAction = async(e)=>{
        e.preventDefault();
        const searchQueryText = this.state.val;
        alert(searchQueryText);
    }

    render(){
        return(
            <>
                <SearchBar queryText={val=>this.state.val = val}/>
            </>
        )
    }
}

export default App
